html{
    position: relative;
    min-height: 100%;
}
body{
    margin-bottom: 120px;
}

/* Everything but the jumbotron gets side spacing for mobile first views */
.header,
.marketing,
.footer {
    padding-left: 15px;
    padding-right: 15px;
}

.footer {
    text-align: center;
    border-top: 1px solid black;
    padding-top: 15px;
    margin-top: 20px;

    position: absolute;
    bottom: 0;
    width: 100%;
    height: 118px;
    background-color: #f5f5f5;
}
.footer-github{
    letter-spacing: 3px;
}

.jumbotron {
    text-align: center;
}

.title {
    display: flex;
    justify-content: center;
    align-items: center;
}
.title img {
    width: 62px;
    height: 62px;
    margin-right: 6px;
}

#titulo{
    font-family: prototype;
}

.container-fluid {
    max-width: 730px;
}

#carregando {
    z-index: 10;

    position: absolute;
    background-color: white;
    width: 100%;
    height: 100%;

    text-align: center;
}

.carregando {
    position: relative;
    top: 45%;
}

#lista, #query {
    display: none;
}

/* Small devices (tablets, 768px and up) */
#lista {
    margin-top: 60px;
    padding-top: 60px;
}

.nomeCurso {
    text-align: center;
}

/* Medium devices (desktops, 992px and up) */
@media (min-width: 992px) {
    #lista {
        margin-top: 60px;
        padding-top: 60px;
    }
}
#query {
    position: fixed;
    max-height: 100px;
    width: 100%;
    padding-bottom: 10px;
    padding-top: 10px;
    background-color: black;
    top: 0px;
    overflow: hidden;
}

#query.aberta{
    animation-duration: 1s;
    animation-iteration-count: 1;
    animation-name: abrirBarra;
    max-height: 100%;
}

#query.fechada{
    animation-duration: 0.25s;
    animation-iteration-count: 1;
    animation-name: fecharBarra;
    max-height: 100px;
}

@keyframes abrirBarra {
  from {
    max-height: 100px;
  }
  to {
    max-height: 100%;
  }
}

@keyframes fecharBarra {
  from {
    max-height: 100%;
  }
  to {
    max-height: 100px;
  }
}


